'use strict';
const allSections = document.querySelectorAll('.hidden'),
  imgContent = document.querySelectorAll('.section img'),
  objOptions = { root: null, threshold: 0.3 },
  sectionObserver = new IntersectionObserver(callBackFunction, objOptions);
function callBackFunction(e, t) {
  e.forEach((e) => {
    e.target.getAttribute('class');
    e.isIntersecting
      ? e.target.classList.remove('hidden')
      : e.target.classList.add('hidden');
  });
}
allSections.forEach((e) => {
  e && sectionObserver.observe(e);
});
